const CHANGE_DATE = 'SHOW-CHANGE_DATE';

const getDate = () => {
    let months = [
        'January',
        'February',
        'March',
        'April',
        'May',
        'June',
        'July',
        'August',
        'September',
        'October',
        'November',
        'December',
    ];
    let today = new Date();
    let date = `${today.getDate()} ${months[today.getMonth()]} ${today.getFullYear()}`;
    return date;
}


const initialState = {
    day: getDate(),
    today: new Date(),
}

export default function dateReducer(state = initialState, action) {

    switch (action.type) {
        case CHANGE_DATE: {
            debugger
            return {...state, today: action.value}
        }
        default:
            return state;
    }
}

export const onChangeDate = (value) => ({
    type: CHANGE_DATE, payload: {value}
});
