import React, {useState} from 'react';
import s from './Tracker.module.css';
import Sync from './Sync/Sync';
import Timer from './Timer/Timer';
import {useSelector} from "react-redux";


const Tracker = () => {
    const [on, setOn] = useState(false);

    const menuOn = () => {
        setOn(!on)
}

const syncIssuesList = useSelector(state => state.sync.syncIssues);

return (
    <div className={s.tracking}>
        <div>
            <div className={s.header}>
                <p>Time tracking</p>
                <div>
                    <button className={s.buttonBurger} onClick={menuOn}>
                        <span className={s.burgerSpan}/></button>
                    {on && <ul className={s.burgerMenu} onBlur={menuOn}>
                        <li>Jira Link</li>
                        <li>Duplicate</li>
                        <li>Add to favorite</li>
                        <li>Delete</li>
                    </ul>}
                </div>
            </div>
        </div>
        <Timer/>
        <Sync syncIssuesList={syncIssuesList}/>
    </div>
)
}

export default Tracker;
