import React from "react";
import TimeRangeSlider from 'react-time-range-slider';
import s from "./TimeSlider.module.css";
import accept from "../../../../img/accept.svg";
import decline from "../../../../img/decline.svg";


class RangeSlider extends React.Component {
    constructor(props) {
        super(props);
        this.changeStartHandler = this.changeStartHandler.bind(this);
        this.timeChangeHandler = this.timeChangeHandler.bind(this);
        this.changeCompleteHandler = this.changeCompleteHandler.bind(this);
        this.state = {
            value: {
                start: "07:00",
                end: "19:00"
            }
        }
    }

    changeStartHandler(time) {
        console.log("Start Handler Called", time);
    }

    timeChangeHandler(time) {
        this.setState({
            value: time
        });
    }

    changeCompleteHandler(time) {
        console.log("Complete Handler Called", time);
    }

    render() {
        return (
            <div className={s.wrapperWorklog}>
                <div className={s.worklogWindow}>
                    <div className={s.header}><p>New worklog</p></div>
                    <div className={s.container}>
                        <TimeRangeSlider
                            disabled={false}
                            format={24}
                            maxValue={"19:00"}
                            minValue={"07:00"}
                            name={"time_range"}
                            onChangeStart={this.changeStartHandler}
                            onChangeComplete={this.changeCompleteHandler}
                            onChange={this.timeChangeHandler}
                            step={30}
                            value={this.state.value}
                            formatLabel={this.props.formatLabel}/>
                        <div className={s.worklogInputs}>
                            <label>Worklog name</label>
                            <input type="text" className={s.nameInput} value={this.props.name}
                                   onChange={this.props.handleChangeName}/>
                            <label>Issue</label>
                            <input type="text" className={s.issueInput} value={this.props.label}
                                   onChange={this.props.handleChangeLabel}/>
                        </div>
                        <div className={s.worklogButtons}>
                            <button className={s.acceptButton} onClick={this.props.pushWorklog}>
                                <img className={s.accept} src={accept} alt='accept'/>
                            </button>
                            <button className={s.declineButton} onClick={this.props.showWorklog}>
                                <img className={s.decline} src={decline} alt='decline'/>
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default RangeSlider;
