const NEW_WORKLOG = 'NEW_WORKLOG';

const initialState = {
    issues: [
        {
            time: {
                from: '09:00',
                by: '10:00'
            },
            label: 'JRM-310',
            name: 'Team standup',
            timer: '01:00:00',
        },
    ],
}

export default function issuesReducer(state = initialState, action) {
    switch (action.type) {
        case NEW_WORKLOG:
            let issues = [...state.issues];
            let timer = action.stopwatchTime;
            let time = action.value;
            let from = `0${time[0]}:00`;
            let by = `${time[1]}:00`;
            issues.push(
                {
                    time: {
                        from: from,
                        by: by
                    },
                    label: action.label,
                    name: action.name,
                    timer: timer.hours + ':' + timer.minutes + ':' + timer.seconds,
                });
            return {
                ...state,
                issues
            };
        default:
            return state;
    }
}
