const NEW_WORKLOG = 'NEW_WORKLOG';

const initialState = {
    issues: [
        {
            time: {
                from: '09:00',
                by: '10:00'
            },
            label: 'JRM-310',
            name: 'Team standup',
            timer: '01:00:00',
        },
    ],
}

export default function issuesReducer(state = initialState, action) {
    switch (action.type) {
        case NEW_WORKLOG:
            let issues = [...state.issues];

        function formatTime(time) {
            let from = Math.trunc(time[0] / 60);
            let from2 = time[0] % 60;
            let by = Math.trunc(time[1] / 60);
            let by2 = time[1] % 60;

            if (time[0] < 600) {
                from = '0' + from;
            }
            if (time[1] < 600) {
                by = '0' + by;
            }
            if (from2 === 0) {
                from2 = '0' + from2;
            }
            if (by2 === 0) {
                by2 = '0' + by2;
            }

            let fromResult = from + ':' + from2;
            let byResult = by + ':' + by2;
            return [fromResult, byResult];
        }

            let resultTime = formatTime(action.payload.sliderValue);
            let from = resultTime[0];
            let by = resultTime[1];
            //
            // let formatStopwatchTime = (time) => {
            //     let hour = Math.floor(time/60);
            //     let minutes = Math.floor((time%60)/60);
            //     let seconds = Math.floor();
            //     if (hour < 10) { hour = '0' + hour}
            //     if (minutes < 10) { minutes = '0' + minutes}
            //     if (seconds < 10) { seconds = '0' + seconds}
            //     let stopWatchTime = hour + ':' + minutes + ':' + seconds;
            //     return stopWatchTime;
            // }
            let formatStopwatchTime = (time) => {
                let seconds = time % 60;
                let minutes = (time / 60) % 60;
                let hour = time / 3600 % 24;
                console.log(minutes);
                if (hour < 10) {
                    hour = '0' + hour
                }
                if (minutes < 10) {
                    minutes = '0' + minutes
                }
                if (seconds < 10) {
                    seconds = '0' + seconds
                }
                let stopWatchTime = hour + ':' + minutes + ':' + seconds;
                return stopWatchTime;
            }

            // let stopwatchTime = formatStopwatchTime(action.payload.stopwatchTime);
            let stopwatchTime = formatStopwatchTime(610);

            issues.push(
                {
                    time: {
                        from: from,
                        by: by
                    },
                    label: action.payload.label,
                    name: action.payload.issue,
                    timer: stopwatchTime,
                });
            return {
                ...state,
                issues
            };
        default:
            return state;
    }
}

// export const submitForm = (label, issue) => ({
//     type: NEW_WORKLOG, payload: {label, issue}
// });

export const submitForm = (stopwatchTime, sliderValue, label, issue) => ({
    type: NEW_WORKLOG, payload: {stopwatchTime, sliderValue, label, issue}
});
