const SHOW_CALENDAR = 'SHOW-CALENDAR';

const initialState = {
    today : new Date ()

}

export default function dateReducer(state=initialState, action){

    switch (action.type) {
        case SHOW_CALENDAR: {
    return {...state, showCalendar: action.payload}
        }
        default:
            return state;
    }
}
