import React, {useState} from 'react';
import s from './TimerNew.module.css';
import {Field, reduxForm} from "redux-form";
import {Input} from "../../../../common/Forms/FormsControl";
import Stopwatch from "./Stopwatch/Stopwatch";
import TimeSlider from "../TimeSlider/TimeSlider";
import {useDispatch, useSelector} from "react-redux";
import {setActiveTimer, submitForm} from "../../../../store/reducers/issuesReducer";

const TimerNew = (props) => {

    const isActiveTimer = useSelector(state => state.issuesList.isActiveTimer);
    const [onSlider, setonSlider] = useState(false);
    const [stopwatchTime, setStopwatchTime] = useState(0);
    const [sliderValue, setSliderValue] = useState([540, 690]);
    const dispatch = useDispatch();
    const initialLabel = useSelector(state => state.issuesList.initialLabel);
    const initialName = useSelector(state => state.issuesList.initialName);
    let initalValues = {
        label: initialLabel,
        issue: initialName
    }

    const toggleSlider = () => {
        setonSlider(!onSlider);
    };

    let onSubmitForm = (data) => {
        dispatch(submitForm(stopwatchTime, sliderValue, data.label, data.issue));
        setStopwatchTime(0);
        setSliderValue([540, 690]);
        activateTimer();
        setonSlider(false);
    }

    let activateTimer = () => {
        dispatch(setActiveTimer());
    }

    return (
        <div>
            {isActiveTimer || initialLabel? null :
                <div className={s.newWorklog}>
                    <button className={s.appendWorklog} onClick={activateTimer}/>
                    <p className={s.label}>NEW WORKLOG</p>
                </div>}
            {isActiveTimer || initialLabel ? <div className={s.timerOn}>
                <IssueForm initialValues={initalValues}/>
                <Stopwatch toggleSlider={toggleSlider} stopwatchTime={stopwatchTime} setStopwatchTime={setStopwatchTime} isActive={isActiveTimer} activateTimer={activateTimer}/>
                {onSlider ? <TimeSlider onSubmit={onSubmitForm} sliderValue={sliderValue} setSliderValue={setSliderValue}/>: null}
            </div> : null}
        </div>
    )
};

let IssueForm = (props) => {
    return (
        <div>
            <form>
                <Field name={'label'} component={Input} placeholder={'New Label'}/>
                <Field name={'issue'} component={Input} placeholder={'New Issue'}/>
            </form>
        </div>
    )
}

IssueForm = reduxForm(
    {
        form: 'issueForm'
    }
)(IssueForm);

export default TimerNew;
