import React, {useState} from 'react';
import s from './Issue.module.css';
import play from '../../../../img/play.svg';

const Issue = (props) => {
    const [on, setOn] = useState(false);
    const [show, setShow] = useState(false);

    const toggleOn = () => {
        setOn(true);
    }

    const toggleOff = () => {
        setOn(false);
    }

    const showBurger = () => {
        setShow(true);
    }

    const hideBurger = () => {
        setShow(false);
    }

    return (
        <div className={s.issue}>
            <div className={on ? s.issueItemsActive : s.issueItems} onMouseEnter={toggleOn} onMouseLeave={toggleOff}>
                <div className={s.time}><strong>{props.timeIssueFrom}</strong> â€” {props.timeIssueBy}</div>
                <div className={s.indicatorComplite}></div>
                <div className={s.name}>
                    <p className={s.label}>{props.label}</p>
                    <p className={s.text}>{props.text}</p>
                </div>
                <div className={s.progress}>
                    <span className={s.progressComplite}></span>
                </div>
                <div className={s.timer}>
                    <p>{props.timer}</p>
                </div>
                <button className={s.playButton}>
                    <img src={play} alt='play'/>
                </button>
            </div>
            {on ? <div className={s.activeBar} onMouseEnter={toggleOn} onMouseLeave={toggleOff}>
                <button className={s.burger} onMouseEnter={showBurger}>
                    <span></span>
                  {show ?  <ul className={s.burgerMenu} onMouseLeave={hideBurger}>
                        <li>Jira Link</li>
                        <li>Duplicate</li>
                        <li>Add to favorite</li>
                        <li>Delete</li>
                    </ul> : null}
                </button>
            </div> : null}
        </div>
    );
}

export default Issue;
