import React, { useState, useEffect } from "react";
import s from './TimeSlider.module.css'
import accept from '../../../../img/accept.svg'
import decline from '../../../../img/decline.svg'
import { Slider } from "@material-ui/core";
import { makeStyles, withStyles } from "@material-ui/core/styles";

const useStyles = makeStyles({
  root: {
    width: 543,
  }
})

function valueLabelFormat(value) {
  let a = Math.trunc(value/60);
  let b = value%60;
  return `${a} : ${b}`;
}

const RangeSlider = withStyles({
  root: {
    color: '#FFCC40',
  },
  track: {
    height: 10,
  },
  rail: {
    color: '#d8d8d8',
    opacity: 1,
    height: 10,
    borderRadius: 4
  },
  thumb: {
    height: 16,
    width: 16,
    backgroundColor: '#FFCC40',
    marginTop: -3,
    border: '6px solid #fff'
  },
  valueLabel: {
    left: -15,
    '& *': {
      color: "transparent"
    },
  }
})(Slider);

const TimeSlider = (props) => {


  const handleChange = (event, newValue) => {
    props.setValue(newValue);
  };

  const classes = useStyles();

  return (
    <div className={s.wrapperWorklog}>
      <div className={s.worklogWindow}>
        <div className={s.header}><p>New worklog</p></div>
        <div className={s.container}>
          <div className={classes.root}>
            <RangeSlider
              value={props.value}
              onChange={handleChange}
              valueLabelDisplay="on"
              aria-labelledby="range-slider"
              getAriaValueText={valueLabelFormat}
              valueLabelFormat={valueLabelFormat}
              min={420}
              max={1140}
              step={30}
            />
          </div>
          <div className={s.worklogInputs}>
            <label>Worklog name</label>
            <input type="text" className={s.nameInput} value={props.name} onChange={props.handleChangeName} />
            <label>Issue</label>
            <input type="text" className={s.issueInput} value={props.label} onChange={props.handleChangeLabel} />
          </div>
          <div className={s.worklogButtons}>
            <button className={s.acceptButton} onClick={props.pushWorklog}>
              <img className={s.accept} src={accept} alt='accept' />
            </button>
            <button className={s.declineButton} onClick={props.showWorklog}>
              <img className={s.decline} src={decline} alt='decline' />
            </button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default TimeSlider;
