import React, {Component} from "react";
import {Calendar} from "react-calendar";
import 'react-calendar/dist/Calendar.css'
import s from "./Calendar.module.css";
import './Calendar.scss'
import calendarLogo from "../../../../img/calendar.svg";
import store from "../../../../store/configureStore";

export default class CalendarShow extends Component {
    getinitialState() {
        return {
            nowDate: store.getState().date.day,
            date: new Date()
        }
    }

    initialState = this.getinitialState();

    state = {
        ...this.initialState,
        on: false
    }

    onChange = date => this.setState({date})

    toggle = () => {
        this.setState({
            on: !this.state.on
        })
    }

    formatShortWeekday = (locale, date) => ['S', 'M', 'T', 'W', 'T', 'F', 'S'][date.getDay()]

    render() {
        return (
            <div>
                <div className={s.data}>
                    <button className={s.changeData} onClick={this.toggle}>{this.state.nowDate}</button>
                    <img src={calendarLogo} alt='calendar'/>
                </div>
                <div className={s.calendar}>{this.state.on &&
                <Calendar className={'react-calendar'} onChange={this.onChange} value={this.state.date} locale={'en-EN'}
                          formatShortWeekday={this.formatShortWeekday}
                          tileContent={(date, view) => <span className={'meter'}></span>}/>}
                </div>
            </div>
        )
    }
}
