import React, {useEffect, useState} from 'react';
import s from './Stopwatch.module.css';


const Stopwatch = () => {
    const [second, setSecond] = useState("00");
    const [minute, setMinute] = useState("00");
    const [hour, setHour] = useState("00");
    const [isActive, setIsActive] = useState(true);
    const [counter, setCounter] = useState(5000);

    useEffect(() => {
        let intervalId;

        if (isActive) {
            intervalId = setInterval(() => {
                const secondCounter = counter % 60;
                const minuteCounter = Math.floor(counter / 60);
                const hourCounter = Math.floor(counter / 3600);

                let computedSecond =
                    String(secondCounter).length === 1
                        ? `0${secondCounter}`
                        : secondCounter;
                let computedMinute =
                    String(minuteCounter).length === 1
                        ? `0${minuteCounter}`
                        : minuteCounter;
                let computedHour =
                    String(hourCounter).length === 1
                        ? `0${hourCounter}`
                        : hourCounter;

                setSecond(computedSecond);
                setMinute(computedMinute);
                setHour(computedHour);

                setCounter((counter) => counter + 1);
            }, 1000);
        }

        return () => clearInterval(intervalId);
    }, [isActive, counter]);

    function stopTimer() {
        setIsActive(false);
        setCounter(0);
        setSecond("00");
        setMinute("00");
        setHour("00");
    }

    return (
        <div className={s.container}>
            <div className={s.time}>
                <span className={s.hour}>{hour}</span>
                <span>:</span>
                <span className={s.minute}>{minute}</span>
                <span>:</span>
                <span className={s.second}>{second}</span>
            </div>
        </div>
    );
};

export default Stopwatch;
