import React, {useState} from 'react';
import s from './TimerNew.module.css';

const TimerNew = (props) => {

    const [onTimer, setOnTimer] = useState(false);

    const startTimer = () => {
        setOnTimer(true);
    };

   return (
       <div>
           {onTimer? null :
               <div className={s.newWorklog}>
                   <button className={s.appendWorklog} onClick={startTimer}/>
                   <p className={s.label}>NEW WORKLOG</p>
               </div> }
           {onTimer? <div className={s.timerOn} >
               <input className={s.issue} type="text" />
               <input className={s.label} type="text"  />
               <div className={s.stopwatch}>00:00:00</div>
               <div className={s.buttons}>
                   <button className={`${s.button} ${s.stopBtn}`} />
                   <button className={`${s.button} ${s.pauseBtn}`}/>
               </div>
           </div> : null}
       </div>
   )
};

export default TimerNew;
