const NEW_WORKLOG = 'NEW_WORKLOG';

const initialState = {
    issues: [
        {
            time: {
                from: '09:00',
                by: '10:00'
            },
            label: 'JRM-310',
            name: 'Team standup',
            timer: '01:00:00',
        },
    ],
}

export default function issuesReducer(state = initialState, action) {
    switch (action.type) {
        case NEW_WORKLOG:
            let issues = [...state.issues];

        function formatTime(time) {
            let from1 = Math.trunc(time[0] / 60);
            let from11 = time[0]-from1;

            let from2 = Math.trunc(time[1] / 60);
            let from22 = time[1]-from2;



            if (time[0] < 600) {
                from1 = '0' + from1;
            }
            if (time[1] < 600) {
                from2 = '0' + from2;
            }
            if (from11 === 0) {
                from11= '0' + from11;
            }
            if (from22 === 0) {
                from22= '0' + from22;
            }






            let a = Math.trunc(time[0] / 60);
            let b = time[1] % 60;
            if (time[0] < 600) {
                a = '0' + a;
            }
            if (b === 0) {
                b = '0' + b;
            }
            return [a,b];
        }

            let resultTime = formatTime(action.payload.sliderValue);
            let from = resultTime[0];
            let by = resultTime[1];

            let formatStopwatchTime = (time) => {
                let hour = Math.floor(time/60);
                let minutes = Math.floor((time%60)/60);
                let seconds = Math.floor(((hour+minutes)%time)/60);
                if (hour >= 10) { hour = '0' + hour}
                if (minutes >= 10) { minutes = '0' + minutes}
                if (seconds >= 10) { seconds = '0' + seconds}
                let stopWatchTime = hour + ':' + minutes + ':' + seconds;
                return stopWatchTime;
            }

            let stopwatchTime = formatStopwatchTime(action.payload.stopwatchTime);

            issues.push(
                {
                    time: {
                        from: from,
                        by: by
                    },
                    label: action.payload.label,
                    name: action.payload.issue,
                    timer: stopwatchTime,
                });
            return {
                ...state,
                issues
            };
        default:
            return state;
    }
}

