import React, {useEffect, useState} from 'react';
import s from './Stopwatch.module.css';


const Stopwatch = () => {
    const [second, setSecond] = useState(0);
    const [minute, setMinute] = useState(59);
    const [hour, setHour] = useState(0);
    const [isActive, setIsActive] = useState(true);
    const [counter, setCounter] = useState(56);

    useEffect(() => {
        let intervalId;

        if (isActive) {
            intervalId = setInterval(() => {

                setSecond(counter);

                if (minute === 60) {
                    setHour(hour+1);
                    setMinute(0);
                }
                if (second === 60) {
                    setMinute(minute+1);
                    setSecond(0);
                    setCounter(0);
                }


                setCounter((counter) => counter + 1);
            }, 1000);
        }

        return () => clearInterval(intervalId);
    }, [isActive, counter]);

    function stopTimer() {
        setIsActive(false);
        setCounter(0);
        setSecond(0);
        setMinute(0);
        setHour(0);
    }

    return (
        <div className={s.container}>
            <div className={s.time}>
                <span className={s.hour}>{hour}</span>
                <span>:</span>
                <span className={s.minute}>{minute}</span>
                <span>:</span>
                <span className={s.second}>{second}</span>
            </div>
        </div>
    );
};

export default Stopwatch;
